func makeConfiguration() gameoflife.Cells {
    var conf gameoflife.Cells
    for i, row := range configuration {
        for j, cell := range row {
            if cell != '.' {
                x := int64(j)
                y := int64(i)
                conf = append(conf, gameoflife.Cell{x, y})
            }
        }
    }

    return conf
}
